import UserContext from "../../Contexts/UserContext";
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import React, { useEffect, useState, useContext } from "react";
import { Link } from "react-router-dom";

function Filter(props){
    const {data, selectedFilter} = useContext(UserContext);
    console.log(data)

    switch(selectedFilter){
        case 'option1':
            return (
                <div>
                  {data.map((item) => (
                    <div key={item.id}>
                      <h2>{item.title}</h2>
                      <p>{item.description}</p>
                      <p>{item.category}</p>
                      <p>{item.price}</p>
                      <p>{item.created_at}</p>
                      <p>{item.user_id}</p>
                    </div>
                  ))}
                </div>
              );
            break;
        case 'option2':
            return (
                <div>
                  {data.map((item) => (
                    <div key={item.id}>
                      <h2>Users who posted with both categories selected:</h2>
                      <h3>{item.username}</h3>
                    </div>
                  ))}
                </div>
              );
            break;
        case 'option3':
            return (
                <div>
                  {data.map((item) => (
                    <div key={item.id}>
                      <h2>{item.title}</h2>
                      <p>Description: {item.description}</p>
                      <p>Category: {item.category}</p>
                      <p>Price: {item.price}</p>
                      <p>Created: {item.created_at.slice(0,10)}</p>
                      <p>{item.user_id}</p>
                    </div>
                  ))}
                </div>
              );
            break;
        case 'option4':
            return (
                <div>
                  {data.map((item) => (
                    <div key={item.id}>
                      <h2>User who posted the most number of items since 5/1/2020:</h2>
                      <p>User: {item.username}</p>
                      <p>Number of Items: {item.num_items}</p>
                    </div>
                  ))}
                </div>
              );
            break;
        case 'option5':
            return (
                <div>
                <h2>Users who are favorited by both users selected:</h2>
                  {data.map((item) => (
                    <div key={item.id}>
                      <p>User: {item.favorite_user_id}</p>
                    </div>
                  ))}
                </div>
              );
            break;
        case 'option6':
            return (
                <div>
                  {data.map((item) => (
                    <div key={item.id}>
                      <h2>All the users who never posted any 'excellent' items: an item is excellent if at least three reviews are excellent.</h2>
                      <p>User: {item.username}</p>
                    </div>
                  ))}
                </div>
              );
            break;
        case 'option7':
            break;
        case 'option8':
            break;
        case 'option9':
            break;
        case 'option10':
            break;
        
        default:
            break;
    }

    // return (
    //     <ul>
    //         {Object.values(data).map((value, index) => (
    //         <li key={index}>{value}</li>
    //         ))}
    //     </ul>
    //   );
}

export default Filter;
