import React, {useState} from "react";
import './Register.css';
import Axios from 'axios';
import { useNavigate, Link } from "react-router-dom";

function Register() {
    const [usernameReg, setUsernameReg] = useState("");
    const [passwordReg, setPasswordReg] = useState("");
    const [passwordConf, setPasswordConf] = useState("");
    const [emailReg, setEmailReg] = useState("");
    const [firstNameReg, setFirstNameReg] = useState("");
    const [lastNameReg, setLastNameReg] = useState("");
    const navigate = useNavigate();

    //calls a axios post method that pushes data to the server side
    const register = ()=>{
        //if the password confirmation is not the same as the password, it will alert the user
        if(passwordConf != passwordReg){
            window.alert("passwords do not match");
        } else {
            Axios.post('http://localhost:3001/register', 
            {firstName: firstNameReg, lastName: lastNameReg, 
                username: usernameReg, email: emailReg, password: passwordReg
            }).then((response) =>{
                console.log(response)
            });
        }
    }

    return (
    <div>
        <div className="register">
            Register Here!
        </div>
        <div className="container">
            <div>
                Enter First and Last Name: <br/>
                <input 
                    className="input"
                    type="text" 
                    onChange={((e)=>
                        {setFirstNameReg(e.target.value)}
                    )}
                >
                </input>
                <input 
                    type="text" 
                    onChange={((e)=>
                        {setLastNameReg(e.target.value)}
                    )}
                >
                </input>
            </div>
            <div>
                Enter Username: <br/>
                <input 
                    type="text" 
                    onChange={((e)=>
                        {setUsernameReg(e.target.value)}
                    )}
                >
                </input>
            </div>
            <div>
                Enter Email: <br/>
                <input 
                    type="text" 
                    onChange={((e)=>
                        {setEmailReg(e.target.value)}
                    )}
                >
                </input>
            </div>
            <div>
                Enter Password: <br/>
                <input 
                    type="text" 
                    onChange={((e)=>
                        {setPasswordReg(e.target.value)}
                    )}
                >
                </input>
            </div>
            <div>
                Confirm Password: <br/>
                <input 
                    type="text" 
                    onChange={((e)=>
                        {setPasswordConf(e.target.value)}
                    )}
                >
                </input>
            </div>
            <button onClick={register}>Continue</button>
        </div>
    </div>
    );
}

export default Register;
